css:
@-moz-document domain("jupiterbroadcasting.com") {

body {
	background: #888;
	color: #000;
}
.videoitem {
	background: #aaa;
  border-radius: 4px;
	border: 1px solid #666;
  box-shadow: 0 1px 2px rgba(0,0,0,.3);
}
.thumbnail {
	border: none;
	background: none;
	box-shadow: none;
}
.navbar-default .navbar-nav > li > a:hover,
a:link,
a {
	color: #036;
}
.dropdown-menu,
.navbar-default,
#sidebar .widget-title {
	background: #bbb;
	border-color: #666;
}
#searchForm,
#sidebar .widget {
	background: #aaa;
	border-color: #666;
}
#thevideoinside,
#thevideo {
	background: #333;
}
#postcontent,
#ratingsblock li,
#videodets {
	border-color: #666;
}

}
