css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("eztv.ag") {
    #header_logo, #header_link, #header_link_holder, #site_menu, #gap, #line, .forum_thread_header, .forum_thread_header_end, #at4-count, #at-share-dock, .piro_overlay, .pirobox_content, #atssh, #service-icons-0, .at4-share-outer, .addthis-smartlayers, .addthis-smartlayers-desktop {
        display: none;
    }
    *,
    body {
        font-family: Tahoma, Verdana, Segoe, sans-serif;
        font-size: 16px
    }
    a {
        font-size: 16px
    }
    a.epinfo {
        color: black;
        font-family: Tahoma, Verdana, Segoe, sans-serif;
        font-size: 16px;
        background-color: white;
        line-height: 1.4em
    }
    .forum_thread_post {
        background-color: white;
    }

}
