css:
@-moz-document domain("tgs-clan.pl") {
.username-coloured {
    color: #59991C !important;
}
.user-reputation span {
    color: #82C140 !important;
}
.bg1 {
    background-color: #333;
}
.bg2 {
    background-color: #333;
}
.postbody .content {
    color: #D6D6D6;
}

html,
body {
    background: #222 !important;
    background-image: none !important;
}
#logo img {
    max-height: 40px;
    margin-top: 5px;
    margin-left: 10px;
    margin-bottom: 5px;
}
.forum_unread,
.topic_unread {
    background-color: #1E3A2F;
}
.topic_unread_hot {
    background-color: #513B14;
}
div.mChatBG1 {
    background: #222 !important;
    border: none;
}
div.mChatBG2 {
    background: #333;
    border: none;
}
input.mChatText {
    width: 50%;
    margin: 0;
    float: left;
}
}
