@-moz-document domain("totallyinsanetranlation.com") {
div,.commentlist article.comment,h1,h2,h3 {
    color:#686868!important;
    background:none!important;
    background-image:none!important;
    background-color:#282828!important;
    box-shadow: 0 -99px 0 rgba(0,0,0,0.09) inset;
}
p, a {
    color:#989898!important;
}
.site-footer #footer-body .nav-tabs > li > a, .nav-tabs > li > a, .nav-tabs {
    background: rgb(36, 36, 36)!important;
}
.widget-area .widget_recent_comments li, .widget-area .widget_recent_entries li {
    border-bottom: 1px solid #181818;
}
.post-format-badge i {
    font-size: 0px!important;
}
.bl_background {
    position: fixed;
    z-index: -1;
    top: -5%;
    left: -80%;
    width: 200%;
    height: 2000%;
}
input {
    background-color:#181818!important;
    color:#787878!important;
    border-color:#383838!important;
}
.comments-title,#reply-title {
    text-shadow:none!important;
}
#commentform textarea {
    background:none!important;
    background-color:#181818!important;
    color:#787878!important;
    border:1px!important;
    border-color:#181818!important
}
}
