css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("imdb.com") {
  .rec-outline {
    background-color:#000 !important;
    color:#000;
  }
  .rec-outline:hover {
    color:#fff;
  }
}

@-moz-document url-prefix("http://www.imdb.com/title/tt") {
  .summary_text, #titleStoryLine > div[itemprop="description"] > p {
    background-color:#000 !important;
    color:#000;
  }
  .summary_text:hover, #titleStoryLine > div[itemprop="description"]:hover > p {
    color:#fff;
  }
}
