css:
@-moz-document domain("eksisozluk.com") {

.light-theme #advanced-search-form, .light-theme .dropdown .dropdown-menu, .light-theme .popped-player, body.light-theme, body.light-theme>header, .light-theme .modal, .light-theme #in-topic-search-options {background-color:#f0f0f0!important;}
#top-navigation .dropdown-toggle:after {content:"menü"!important;}
.editform {background-color:transparent!important;}
.editform fieldset {padding:0!important;}
#threads li article.unread h2 {color:#80c14b!important;}
#entry-item-list>li:not(:first-child) {border-top:1px solid #ccc;}
.topic-list li>a.empty-index-item {color:#bbb!important;}

#entry-item-list .read-more-link-wrapper {display:none!important;}
#entry-item-list .content {max-height:100%!important;}
sup.ab a {font-size:1rem !important;color:#d00 !important;}
sup.ab a:after {content:attr(title) !important;font-size:1rem !important;color:#d00 !important;}
#nav-extra-buttons {display:none!important;}

}
