css:
@-moz-document domain("habrahabr.ru"), domain("geektimes.ru"), domain("megamozg.ru") {
@media(min-width:1024px){
    div#layout {
        width: 960px;
    }
}
@media(max-width:1024px){
    div#layout {
        min-width: 600px;
        max-width: 800px !important;
    }
    #layout .inner{
        /*padding-left: 32px;*/
        padding-left: 10px;
        padding-right: 0;
    }
    #navbar .nav_panel{
        left: -32px;
        background: #fff;
    }
    /*#navbar:hover .nav_panel{
        left: 0;
    }*/
    #navbar .nav_panel .scroll_to_up{
        display: none;
    }
}
}
