css:
@-moz-document domain("hurtworld.com") {
body {
    background-attachment: fixed;
}
.steam > iframe {
	width: 100%;
	height: 190px;
}
#sign-up #signup_email {
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    border-radius: 0;
}
#sign-up #signup_submit {
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    border-radius: 0;
    font-size: 16px!important;
}
#sign-up label {
    position: relative;
    top: 3px;
}
.page-numbers {
    position: relative;
	padding: 0 5px;
}
a.page-numbers {
	color: #1a1a1a;
    font-family: 'Dosis', Arial, sans-serif;
    font-weight: 400;
    font-style: normal;
	text-decoration: none;
	border: 13px solid transparent;
	border-left: 0;
    border-right: 0;
}
#site-header .logo a {
    background: url('http://zapodaj.net/images/4a2ec9f5953c5.png') no-repeat 0 0;
}
}
