css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("codeception.com") {
    
    body {
       background: #333;
    }
    
    body .page, .dropdown-menu, #toc ul {
        background: #555;
        color: #fff;
    }
    
    body h1, body h2, body h3, body h3{
        color: #fff;
    }

    .language-php{
        color: #bbb;
    }
    
    a {
        color: #42bcca;
    }
    
    a:hover, a:focus {
        color: #0ff;
    }
    
    body .dropdown-modules li a, .dropdown-menu > li > a {
        color: #fff;
    }
}
