css:
@-moz-document url-prefix("https://www.zhihu.com"), domain("www.zhihu.com") {
* {-webkit-font-smoothing: subpixel-antialiased !important;}
@font-face {unicode-range: U+3040-30FF;   /* Hiragana, Katakana */
font-family: 'IndependentKana'; src: local('Hiragino Kaku Gothic ProN')}

@font-face {unicode-range: U+FF00-FF1E, U+FF20-FFEF, U+3000-3001, U+3003-303F;   /* BiauDian 1 */
font-family: Biaudian; src: local('Hiragino Kaku Gothic ProN')}

@font-face {unicode-range: U+3002, U+FF1F;   /* BiauDian 2 */
font-family: Biaudian; src: local('Hiragino Sans GB')}

body {font-family: 'Helvetica Neue', Helvetica, Biaudian, 'IndependentKana', 'Hiragino Sans GB', 'Arial Nova', Arial, 'Source Han Sans SC', 'Microsoft YaHei', 'WenQuanYi Micro Hei', sans-serif !important;
}

input {font-family: 'Helvetica Neue', Helvetica, Biaudian, 'IndependentKana', 'Hiragino Sans GB', Arial, sans-serif !important;	font-feature-settings: "palt" !important;
	-moz-font-feature-settings: "palt" !important;
	-webkit-font-feature-settings: "palt" !important;}

.zu-main-content * {
	font-feature-settings: "palt" !important;
	-moz-font-feature-settings: "palt" !important;
	-webkit-font-feature-settings: "palt" !important;
}

.zu-main-sidebar * {
	font-feature-settings: "palt" !important;
	-moz-font-feature-settings: "palt" !important;
	-webkit-font-feature-settings: "palt" !important;
}

.mention-popup * {
	font-feature-settings: "palt" !important;
	-moz-font-feature-settings: "palt" !important;
	-webkit-font-feature-settings: "palt" !important;
}

.zh-tooltip * {
	font-feature-settings: "palt" !important;
	-moz-font-feature-settings: "palt" !important;
	-webkit-font-feature-settings: "palt" !important;
}

.zh-footer * {
	font-feature-settings: "palt" !important;
	-moz-font-feature-settings: "palt" !important;
	-webkit-font-feature-settings: "palt" !important;
}

.ac-renderer * {
	font-feature-settings: "palt" !important;
	-moz-font-feature-settings: "palt" !important;
	-webkit-font-feature-settings: "palt" !important;
}

.top-nav-profile * {
	font-feature-settings: "palt" !important;
	-moz-font-feature-settings: "palt" !important;
	-webkit-font-feature-settings: "palt" !important;
}
}
