css:
@-moz-document domain('moodle-2015-16.stolaf.edu') {
  body {
    background: none !important;
  }
  #inst9144, 
  #inst14, 
  #page-header, 
  #block-region-side-post, 
  .navbar + div.text-center, 
  .navbar, 
  #page-footer {
    display: none !important;
  }
  #region-bs-main-and-pre {
    width: 100%;
  }
  #block-region-side-pre {
    order: 1;
    margin-right: 1em;
  }

  #region-bs-main-and-pre > .row-fluid, #page-content {
    display: flex;
    justify-content: center;
  }
  #region-main {
    flex: 1;
    float: none;
    width: auto;
    order: 2;
  }

  #wrapper {
    border-top: 0;
    padding-bottom:25vh;
  }

  .nav-tabs > li > a {
    cursor: pointer;
  }
  #page {
    max-width: 60em;
  }
  #region-main {
    max-width: 45em;
  }
  #block-region-side-pre {
    max-width: 15em;
  }
}
