css:
@-moz-document domain("skout.com") {
.main-container {
    display: none;
}

.app-chat .tab-chat-body, .app-chat .tab-chat-inbox {
    height: 550px;
}

html, .app-body-bg {
    background: url(https://clairecommando.files.wordpress.com/2012/04/dsc0915.jpg) !important;
    background-size: cover !important;
}

.app-chat .tab-chat-body, .app-chat .tab-chat-inbox {
    background: url(http://freedesignfile.com/upload/2013/01/Seamless-flowers-pattern-3.jpg);
}

.app-chat .chat-body .chat-bubble-left label, .app-chat .tab-chat-body .chat-bubble-right label {
    background: #E96969;
}

.app-chat .tab-chat-body .chat-bubble-right label:after {
    border-left: 6px solid #E96969;
}

.app-chat .chat-body .chat-bubble-left label, .app-chat .tab-chat-body .chat-bubble-left label {
    background: #EF9494;
}

.app-chat .tab-chat-body .chat-bubble-left label:before {
    border-right: 6px solid #EF9494;
}

textarea.form-control.ng-valid.ng-dirty.ng-touched, textarea.form-control.ng-pristine.ng-valid.ng-touched, .tabrow-wrap .tab-open .header-wrap, .header {
    background: url(http://i.imgur.com/xtNhL5F.png);
    border-radius: 0;
}

textarea.form-control.ng-valid.ng-dirty.ng-touched {
    color: white;
}

li.chat-list-li {
    background: url(http://i.imgur.com/1z4B9o1.png) !important;
}

.chat-list .chat-list-ul .chat-list-li, .tabchat-list .chat-list-ul .chat-list-li {
    border-bottom: 1px solid rgba(0,0,0,0.5);
}

.app-chat .tab-chat-body, .app-chat .tab-chat-inbox {
    border-bottom: none;
}
}
