css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://nekto.me/chat/") {
  .top_text, .addthis_toolbox, footer, .nav_header, #search_form  {
    display: none;
  }
  #chat {
    margin-top: 20px; 
  }
  .chat {
    width: 100% !important;;
  }
  #msg {
    width: 100%;
    height: 68px;
  }
  .tbl, #chat, .text_pole, #smiles_table, #head {
    width: 100%;
  }
  #wrapper {
    width: 99%;
  }
  .text_pole input
  {
    right: -4px;
    bottom: 0px;
  }
  .text_pole
  {
    margin: 0px -20px 0px -2px;
  }
  #smiles_table td:nth-child(2) {
    width: 150px;
  }
  #header_left {
    width: 170px;
  }
  #header_right {
    float: none;
    width: auto;
  }
  .pointur {
    color: #666 !important;
  }
}
