css:
@-moz-document domain(mactechnews.de){
  body {
      background-color: black;
  }
  body > form {
      background-color: hsl(0, 0%, 10%);
      color: hsl(0, 0%, 90%);
  }
  div.MtnComment {
      background-color: hsl(0, 0%, 10%) !important;
  }
  h2.Title, h2.MtnNewsListTitle, h2.TitleWithNavigation {
      color: hsl(0, 0%, 90%);
      border-bottom: hsl(0,0%,90%) solid 1px;
  }
  .SynNavigationTitleExtension>*:not(:first-child) {
      border-left: 1px solid hsl(0, 0%, 90%);
  }
  .SynNavigationTitleExtension>*:not(.SynNavigationButtonPressed) {
      color: hsl(0, 0%, 90%) !important;
  }
  .MtnNewsParticle .Title {
      color: hsl(0, 0%, 90%);
  }
  .TexticonQuoteText {
      background-color: hsl(0, 0%, 50%);
      border: 1px solid hsl(0,0%,40%);
  }
  .Form > fieldset {
      background-color: hsl(0, 0%, 10%);
  }
  .SynToolbarDialog fieldset legend {
      color: black;
  }
  #content {
      border-right: .0625em solid hsl(0,0%,90%);
  }
  #panel {
      border-left: 1px solid hsl(0,0%,90%);
  }
  a.MtnNewsListTitle {
      color: hsl(0,0%,80%) !important;
  }
  ul.NewsList li a {
      color: hsl(0, 0%, 90%);
  }
  .SynNavigation>*:not(.SynNavigationButtonPressed) {
      color: hsl(0, 0%, 90%) !important;
  }
  table.SearchResults tr td {
      background-color: hsl(0, 0%, 10%);
      border-top: 1px solid hsl(0, 0%, 20%);
      border-bottom: 1px solid hsl(0, 0%, 20%);
      vertical-align: middle;
  }
  table.SearchResults {
      background-color: hsl(0, 0%, 20%);
  }
  table.SearchResults tr td a {
      color: hsl(0, 0%, 90%);
  }
  .SynPageNavigation>*:not(.SynNavigationButtonSmallPressed){
      color: hsl(0, 0%, 90%) !important;
  }

  .ForumIndex ul.ForumTopicList li h4 a {
      color: hsl(0,0%,90%);
  }
  .ForumIndex ul.ForumTopicList li h4 {
      border-bottom: 1px solid hsl(0,0%,90%);
  }
  .ForumIndex ul.ForumTopicList li ul.ForumThreadList li .ForumThreadTitle {
      color: inherit;
  }
  ul.EntryList li>div div, ul.EntryList li>div p a {
      color: hsl(0,0%,90%);
  }
  #ContentPlaceHolder1_CtrlForm_SynPollForm a, .MtnFavorite, #ContentPlaceHolder1_SynPollForm a {
      color: hsl(0,0%,90%) !important;
  }
  .GalleryTopicTitle, .GalleryTopicTitle:active, .GalleryTopicTitle:hover, .GalleryTopicTitle:link, .GalleryTopicTitle:visited, *.GalleryTopicPictureCount, *.GalleryTopicTable, a.MtnPictureScrollTitleLink, .NavigationFilter, .SynNavigationWithTitle, .GalleryThreadNextPictureNavigationBox, .GalleryThreadPreviousPictureNavigationBox, .EventEntry .Time a, .SynMessageEmpty, .SynMessageError, .SynMessageInformation, .SynMessageNote, .SynMessageQuestion, .SynMessageSuccess {
      color: hsl(0,0%,90%);
  }
  .MtnRatingDetails {
      color: black;
  }
  table.EventsCalendar th, table.EventsCalendar td {
      background-color: hsl(0, 0%, 10%);
  }
  table.EventsCalendar tr.Weekend td {
      background-color: hsl(0, 0%, 30%);
  }
  table.EventsCalendar td.EventsWeekDay, table.EventsCalendar td.EventsDay {
      color: hsl(0,0%,70%);
  }
  table.EventsCalendar td.EventsWeekDay strong, table.EventsCalendar td.EventsDay strong {
      color: hsl(0,0%,90%);
  }
  #content > .EventEntry > div {
      background-color: inherit !important;
  }
}
