css:
@-moz-document domain(mactechnews.de) {
  @media screen and (min-width: 1286px){
      body.News_article #content {
          padding-right: 2em;
          width: 54em;
      }
      ARTICLE.MtnArticle.MtnNewsArticle {
          max-width: 49.5em;
      }
      body.News_article #fixed-scroll-1 {
          display: none;
      }	
  }

  @media screen and (max-width: 999px){
      #content {
          max-width: none;
          width: 90%;
      }
      .MtnArticle{
          max-width: none;
      }
  }
  @media screen and (max-width: 604px){
      #content {
          width: auto;
          max-width: none;
      }
  }
}
