css:
@-moz-document regexp("https://apps.facebook.com/avengersalliance/")
{
  #rightCol,
  #pagelet_bluebar,
  #toolbarContainer,
  #bottomContent,
  #xpromoHeaderIframe,
  #xPromoHeaderForm,
  #xPromoHeaderForm,
  #monetization-root,
  #fb-root,
  .accessible_elem,
  .canvas_rating_popout,
  #pagelet_dock,
  canvas_rating_popout,
  #xpromo
  {
    display: none !important;
    top: 0 !important;
    left: 0 !important;
    width: 0% !important;
    height: 0% !important;
    margin: 0px !important;
    padding: 0px !important;
    position: fixed !important;;
  }
  
  #pagelet_canvas_content,
  .canvas_iframe_util
  {
    max-width: unset !important;
    margin-left: 0px !important;
    margin-right: 0px !important;
  }
  
  #lang_en_US
  {
    left: 0px !important;
    position: fixed !important;
  }
}
