css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("members.iracing.com") {
body {
    background: #000000;
    color: #FFFFFF;
}
a {
    color: #FFFFFF;
}
tr {
    background: none;
}
#contentHeader {
    background: none;
}
#contentBody {
    background: none;
}
#contentFooter {
    background: none;
}
.contentBar {
    background: none;
}
.postBody {
    color: #FFFFFF;
}
.postBody a {
    color: #56b6d5;
}
.trPosts {
    background: none;
}
#forum {
    border: 1px solid #b1b1b1;
}
.trDark {
    border: 1px solid #b1b1b1;
    background-color: #555555;
}
#topCorners {
    background: none;
}
#bottomCorners {
    background: none;
}
.trTopBlank {
    background: none;
}
.trBlank {
    background: none;
}
blockquote {
    color: #666666;
}
}
