css:
@-moz-document url(chrome://browser/content/browser.xul) {
  .tabbrowser-tab[pinned][titlechanged]:not([visuallyselected="true"]) > .tab-stack > .tab-content {
    background-image: none !important;
  }
  .tabbrowser-tabs[treestyletab-mode="vertical"] .tabbrowser-tab[pinned][titlechanged]:not([selected="true"]) {
    background: #F2F1F0 none repeat-x scroll 0% 0% !important;
  }
}
