css:
@-moz-document domain(arxiv.com) {

  #header {
      border: none;
      box-shadow: 0 4px 7px rgba(0,0,0,0.7)
  }

  .subheader {
      background-color: #eee;
      color: #000;
      border-top: 1px solid #ddd;
      border-bottom: 1px solid #ddd;
      display: flex;
      align-items:center;
      font-size: 125%;
  }

  .title {
      display: flex;
      justify-content: center;
      align-items:center;
  }

  .extra-services {
      margin-left: 0;
      border: none;
      border: 1vw solid white;
      box-shadow: 0 0 7px rgba(0,0,0,0.7)

  }

  #footer {
      display: flex;
      justify-content: center;
      align-items: center;
      font-size: 125%;

  }

  #footer a{
      margin-left: 0.5vw;
      margin-right: 0.5vw;

  }

}
