css:
@-moz-document domain("stackoverflow.com"), domain("stackexchange.com") {
div#content {
	width: 90% !important;
}
div#question-header {
	width: 100% !important;
}
div#mainbar {
	width: 75% !important;
}
#sidebar {
	width: 20%!important;
}
div.question-summary {
	width: 100% !important;
}

statscontainer {
	min-width: 78px;
	width: 20%;
}
div.question-summary,
div.question-summary div.summary {
	width: 80%;
	display: inline-block;
}

.wmd-button-bar,
.wmd-container {
	width: 100%;
	padding: 0;
}
.wmd-input {
	width: 100.2%;
}
div#answers,
div#questions,
div#answers-header,
div.question,
div.answer,
.wmd-preview,
.grippie,
.comments,
.post-editor,
div.post-text,
.original-question,
form#post-form div.post-editor {
	width: 100% !important;
}
div.answer table,
div.question table {
	table-layout: fixed;
	width: 100% !important;
}
td.votecell {
	width: 50px;
}
tr.comment >:first-child {
	width: 50px;
}
}
