css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("spigotmc.org") {
  body {
    background-color: #444;
  }
  
  .navTabs .navTab.selected .tabLinks, .mainContainer .mainContent, .mainContainer_noSidebar, .secondaryContent, .messageUserBlock div.avatarHolder {
    background-color: #666;
  }
  
  .sectionMain, .primaryContent, .hasFlexbox .messageList .uix_message, .conversation_view .message:first-child, .messageList .message:first-child, .thread_view .pageNavLinkGroup + .section, .messageList .message {
    background-color: #888;
  }
  
  .articleItem .subHeading, .bbCodeBlock .type, .bbCodeBlock pre, .bbCodeBlock .code, .sectionFooter, .message .dark_postrating.likesSummary, .dark_postrating, .bbCodeQuote .quoteContainer, .messageUserBlock h3.userText, .messageUserBlock .extraUserInfo {
    background-color: #aaa;
  }
  
  .resourceUpdate .likesSummary {
    color: rgb(133,133,133);
    background-color: #888;
  }
  
  .avatar .img.s, #QuickSearch:not(.show) #QuickSearchQuery {
    background-color: #ccc;
  }
  
  a[href]:hover {
    color: #222;
  }
  
  a[href] {
    color: #222;
  }
  
  .pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified dt, body .muted, body a.muted, body .muted a, .searchResult .meta a {
    color: #ccc;
  }
  
  .searchResult .meta {
    color: #aaa;
    zoom: 1;
  }
  
  #QuickSearch:not(.show) #QuickSearchQuery {
    color: #444;
  }
  
  .messageUserBlock .arrow span {
    filter: invert(1);
  }
}
