css:
@-moz-document url-prefix("https://synergia.librus.pl") {
span.show.disabled {
    /*display:none !important; /* ukrywa przycisk dodatkowych informacji wyswietlany na lewym boku ekranu*/
    opacity: 0.2;
} 

/* oceny */
table.decorated tbody tr.line0:hover, table.decorated tbody tr.line0:hover th {
    background-color: #FFCECE;
    color: #000;
}

table.decorated tbody tr.line1:hover, table.decorated tbody tr.line1:hover th {
    background-color: #FFCECE;
    color: #000;
}

table.decorated tbody tr.line0 td {
    padding: 0px 5px;
    color: #000;
}

table.decorated tbody tr.line1 td {
    padding: 0px 5px;
    color: #000;
}

/* oceny -> przedmiot */
option[value="16162"] {
	background: #fdd;
    color: #000;
}
option[value="16161"] {
	background: #dfd;
    color: #000;
}
option[value="16160"] {
	background: #ddf;
    color: #000;
}
option[value="20506"] {
	background: #fdd;
    color: #000;
}

table.decorated.form tbody tr.line0:hover {
    background-color: #FFCECE;
    color: #000;
}
table.decorated.form tbody tr.line1:hover {
    background-color: #FFCECE;
    color: #000;
}

input[type=text]:focus, input[type=password]:focus {
    border: 5px #14A919 solid !important;
    border-top: 1px #14A919 solid !important;
    border-bottom: 1px #14A919 solid !important;
    color: #000;
}
input[type=text].small:hover, input[type=password].small:hover {
    width: 80px;
    border: 5px #A91414 solid;
    border-top: 1px #A91414 solid !important;
    border-bottom: 1px #A91414 solid !important;
    color: #000;
}
}
