css:
@-moz-document domain(sync-video.com) { 

#footer.footer, .playlist-left .mtop10.center {
    display: none;
}
#wrap {
    background-color: #191919;
}
#playlist {
    max-height: 410px;
}
.context-data div span, .inline.vmid.small, h5.inline, h5.mtop0, a.orig-link *, #room-title-body, .navbar.navbar-fixed-top {
    color: #37ae9a;
}
.navbar.navbar-fixed-top {
    background-color: #37ae9a;
}
.navbar-inner {
    background: none!important;
}
.well.well-small, .unstyled.ui-sortable li.active, .unstyled.ui-sortable, .chat.mtop20.mbot20, .aspectwrapper {
    background-color: #4d4d4d!important;
    border: 0;
    box-shadow: 0px 0px 10px #000;
    border-radius: 0!important;
}
span.nick {
    color: #3787ae;
}
#chat-area.dialog li:nth-child(odd), .input-msg-fix.chat-input-line {
    background-color: #999999;
}
#chat-area.dialog li:nth-child(even) {
    background-color: #737373;
}
#chat-area.dialog span.error {
    color: #b30000;
}
.span4.playlist-left .unstyled.mtop10 {
    height: 300px!important;
}
}
