css:
@-moz-document url('http://www.dreadcast.net/Main') { 

  

/* L'image de fond de l'AITL */

#zone_dataBox .aitl {background: url('http://www.zupimages.net/up/16/12/fs3a.png') 0 0 no-repeat !important;}



  

/* Modification de la couleur de texte des annonces (question de lisibilité, à adapter selon l'image de fond) */

#zone_dataBox .aitl .principal .texte {color: #000 !important;}

#zone_dataBox .aitl .corporations td.type1, #zone_dataBox .aitl .deces td.type1 {border-bottom: 1px solid #000 !important;

                                                                                 color: #000 !important;}

    





/* La couleur des titres de catégorie (annonces, entreprises, décès) et des liens de bas d'annonce ( Retour, Haut, Contacter l'auteur )*/

 .canaux_imperiaux .couleur2 , .petites_annonces .couleur2 {color: #000 !important}



/* Changement d'emplacement pour la case Rédiger une annonce*/



#zone_dataBox .aitl .actions {height:45px !important;

                              top: 114px !important}

#zone_dataBox .aitl .menu3 {top: 00px !important;}



/* On vire le titre Actions pour qu'il n'empiète pas sur le bouton Eteindre */

#zone_dataBox .aitl .actions .titre {display: none;}


}
