css:
/* set default namespace to XUL */
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/* Menubar colors and text shadow */
menubar {
  text-shadow: none !important;   
  color: white !important;

}

/* Move faveicon to 10px */
#urlbar {
  padding-left: 10px !important;
}

/* Disable text shadows on tabs */
.tabbrowser-tab { 
  text-shadow: none !important; 
}

/*Bookmark toolbar panel height*/
#personal-bookmarks {
  min-height: 30px!important;
  color: black !important;
}

/*Bookmarks toolbar panel font*/
#personal-bookmarks .bookmark-item > .toolbarbutton-text {
  font-weight:bold; 
  color: black !important;
  text-shadow: none !important; 
}

/*normal tab text color*/
.tabbrowser-tab:not([visuallyselected="true"]):not(:hover) > .tab-stack > .tab-content .tab-text {
  /*opacity: 0=transparent; 1=opaque;*/    
  color: rgba(255, 255, 255, 1) !important;
}

/*hovered normal tab text color*/
.tabbrowser-tab:not([visuallyselected="true"]):hover > .tab-stack > .tab-content .tab-text {
  /*font-weight: bold !important;*/
  color: black !important;
}

/*active tab text color*/
.tabbrowser-tab[visuallyselected="true"]:not(:hover) > .tab-stack > .tab-content .tab-text {
  font-weight: bold !important;
  color: black !important;
}

/*hovered active tab text color*/
.tabbrowser-tab[visuallyselected="true"]:hover > .tab-stack > .tab-content .tab-text {
  font-weight: bold !important;  
  color: white !important;
}

#new-tab-button .toolbarbutton-icon
{ 
  -moz-transform: scale(1.3,1.3) !important;
  list-style-image: none !important;
  width: 0 !important;
  padding-left: 16px !important;
  background: url('data:image/png;base64,') center no-repeat !important; 
}
