css:
@-moz-document domain(music.yandex.ru) {
    
html, body, tbody, tr, td, h1, h2, h3, table, strong, font, style
    , .centerblock-wrapper, .stamp__title
    , .head
    , .sidebar-cont
    , .page-album__subtitle
    {
    color: #fff;
    background: #000;
}
    
    .logo__arrow-text {
    color: #fff !important;
}
    
    .sidebar-track__subtitle
    , .album-summary__item
    , .track__ver
    , .album__year
    , .album__version
    {
    color: #D1DE76;
    }
    
.bar
    {
    background: rgba(126, 133, 140, 0.9);
}


.sidebar {
    background: #094822;
}

.link
, a:link
, .nav__tab, .nav__tab:active
, .footer .link, .footer__arrow
{color:#00ffff;text-decoration:none}
a:visited
{color:#ffd700;text-decoration:none}
a:hover
{color: red !important;}
    

}
