css:
@-moz-document domain("www.hitbox.tv") {
#leftcolumn,
#detail-player {
    padding-right: 0px!important;
    padding-left: 0px!important;
}
.chat-main {
    right: 0px!important;
}
#player-transform {
    height: 100vh!important;
    max-height: 100vh!important;
}
#detailpage {
    padding-top: 0px;
}
#chat {
    width: 30%!important;
    top: 0px;
}
#nav-bar,
#chat {
    opacity: 0;
    transition: opacity .3s .1s;
}
#nav-bar:hover {
    opacity: 1;
}
#chat:hover {
    opacity: 1;
}
}
