css:
@-moz-document domain(lingualeo.com) {
.background-green {
    background: #31423A url(http://lingualeo.com/2e53b798/images/layout/v2/bg-img.png) no-repeat center bottom fixed;
}

.b-header.l-center {
    background-color: #434446;
}

.dict-head .dict-title {
    color: #E2E2E2;
}

.l-content-inner {
    background-color: #434446;
}

.dict4-page .sets-toolbar {
    background-color: #434446;
}

.dict-search {
    background: #434446;
}

.title-word {
    background: #434446;
}

.show-kits-inner .menu-item.selected .menu-link, .show-glos-words .menu-item.selected .menu-link, .menu-dark .menu-item.selected .menu-link {
    background-color: #434446;
    box-shadow: none;
}

.show-kits-inner .menu-left-light, .show-glos-words .menu-left-light, .menu-dark .menu-left-light {
    background: #45474A;
}

.l-wrapper-content:after {
    background-color: #45474A;
}


.item-word-translate b {
    color: #C6F9C6;
}
.item-word-translate {
    color: #E2E2E2;
}
}
