css:
@-moz-document regexp("http://www\\.developer\\.com/.+") {
body {
    font-size: 9px;
}

#content > div > div.col.colRight.dimMainRight {
    display: none;
}

#content > div > div.colLeft.dimMainLeft {
    float: none;
    box-sizing: border-box;
    width: 100%;
}

#content > div > div.colLeft.dimMainLeft > div.article > div.toolbox.noBullets.colRight {
    display: none;
}

#content > div > div.colLeft.dimMainLeft > div.article > div.author.colLeft {
    display: none;
}

#content > div > div.colLeft.dimMainLeft > div.article > div.comment_wrapper {
    display: none;
}

#developerBrand-truste-footer {
    display: none;
}

#content > div > div:nth-child(6) {
    display: none;
}

#content > div > div.colLeft.dimMainLeft > div.article {
    font-size: 2em;
}

#content > div > div.colLeft.dimMainLeft > div.article > p {
    font-size: 1em;
    line-height: 1.2;
}

#content > div > div.colLeft.dimMainLeft > div.article > h1 {
    font-size: 1.5em;
    font-weight: bold;
    line-height: 1.2;
}

#content > div > div.colLeft.dimMainLeft > div.article > div.colLeft.dimRelatedArticles_Ads {
    display: none;
}

.globalHeader {
    display: none !important;
}

#header {
    display: none;
}

#headerBottomNav {
    display: none;
}

#EntNetPlanet > div:nth-child(24) {
    display: none;
}

#breadcumb {
    display: none;
}

#EntNetPlanet {
    width: auto;
}

#content > div {
    width: auto !important;
}

#EntNetPlanet > div.vspace {
    display: none;
}

#content > div > div.colLeft.dimMainLeft > div.article > pre {
    width: auto;
    font-size: 1em;
    line-height: 1.1;
    margin: 20px 100px;
    padding: 20px;
}

#content > div > div.colLeft.dimMainLeft > div.article > ul > li {
    font-size: 0.9em;
    line-height: 1.1em;
}
}
