css:
@-moz-document domain("nnmclub.to"), domain("nnm-club.me"), domain("nnm-club.name") {
.audioplayer {
    display: none;
}

.header {
    background-color: #2b4157;
}

.menutable {
    background-color: #2b4157;
    background-image: none;
}

.menutable td {
    border: 2px solid;
    color: #45688B;
}

.pcatHead {
    background-color: #2b4157;
    background-image: none;
}

.request {
    display: none !important;
}

.ui-corner-all {
    border-radius: 0px;
}

.ui-widget-content {
    background-image: none;
}

.ui-widget-header {
    background: none;
    border: none;
}

a.genmed {
    letter-spacing: 0;
}

body {
    background-color: #2b4157;
    color: #2b4157;
}

div .ui-datepicker-header .ui-widget-header .ui-helper-clearfix .ui-corner-all {
    background: #45688B;
}

div.spoiler-head.folded.clickable {
    background-color: #688aad;
}

div.spoiler-wrap {
    background-color: #45688B;
}

div.ui-datepicker-inline.ui-datepicker.ui-widget.ui-widget-content.ui-helper-clearfix.ui-corner-all {
    background-color: #45688B;
}

div.ui-datepicker-title {
    background-color: #45688B;
}

img.pims {
    background-color: #ffffff;
}

span.leech {
    border: 1px solid;
    padding: 0 2px;
}

span.portbody {
    font-family: "TEX GYRE ADVENTOR", "URW Gothic L", Sans-serif;
    font-size: 1.4em;
    letter-spacing: 0.05em;
    line-height: 1.3em;
}

span.postbody {
    font-family: "TEX GYRE ADVENTOR", "URW Gothic L", Sans-serif;
    font-size: 1.3em;
}

span.seed {
    border: 1px solid;
    padding: 0 2px;
}

table {
    border-collapse: collapse;
}

td a img {
    background-color: #2b4157;
}

td.catHead {
    background-color: #45688B;
}

td.catHead a {
    background-color: #45688B;
    color: #ffffff;
}

td.copyright {
    display: none;
}

td.gen {
    background-color: #45688B;
    color: #ffffff;
}

td.genmed {
    border-style: groove;
}

td.gensmall {
    border-color: #b5b5b5;
    border-style: solid;
}

td.row4 {
    display: none;
}

th.thTop {
    background-color: #2b4157 ;
    background-image: none;
    color: #ffffff;
}

thead tr th {
    background-color: #45688B;
}

.topictitle {
    font-size: 14px;
}

a.topictitle:link {
    text-decoration: none;
}
}
