css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


#tt {
  -moz-appearance: none !important;
  border: none !important;
  padding: 3px 0 !important;
  background: #1C2126 !important;
}

#tt-splitter {
  -moz-appearance: none !important;
  width: 4px !important;
  margin-left: 0px !important;
  background: #39424D !important;
  border-right: 1px solid #1C2126 !important;
}


#tt-toolbar {
  -moz-appearance: none !important;
  background-color: #39424D !important;
  border: none !important;
}

#tt-toolbar toolbarbutton {
  -moz-appearance: none !important;
  padding: 4px !important;
  margin: 1px !important;
  border: none !important;
}

#tt-toolbar toolbarbutton:hover,
#tt-toolbar toolbarbutton[checked] {
  -moz-appearance: none !important;
  margin: 0 !important;
  padding: 5px !important; 
  background: #1C2126 !important;
  border: none !important;
}

#tt-toolbar toolbarbutton:not([checked]):hover {
  box-shadow: none !important;
  margin: 0 !important;
  padding: 5px !important;
  background-color: rgba(28,33,38,.5) !important;
}

#tt-toolbar toolbarbutton[checked] {
/*  box-shadow: 0 0 0 0px #1C2126 !important; */
  box-shadow: none !important;
}

ttpinnedtab [anonid="titlechanged-placeholder"][titlechanged] {
  background-image: none !important;
  background-color: #1C2126;
  margin: 3px !important;
}


#tt #tt-close {
  max-width: 0;
  min-width: 0;
  transition: max-width .2s .5s, min-width .2s .5s;
}

#tt:hover #tt-close {
  max-width: 18px;
  min-width: 18px;
  transition: max-width .2s, min-width .2s;
}


#tt-scrollbar {
  width: 10px !important;
}

#tt scrollbar {
  -moz-appearance: none;
  background: none;
  width: 8px;
  margin: 0 2px;
}

#tt scrollbar thumb {
  -moz-appearance: none;
  background: #39424D;
  border: none;
}

#tt scrollbar thumb:hover,
#tt scrollbar thumb:active {
  background: #07090A;
}


#tt-treechildren::-moz-tree-row(current),
#tt-treechildren::-moz-tree-row(selected) {
  border: none !important;
}

#tt-treechildren::-moz-tree-row(selected) {
  background-color: #39424D !important;
}

#tt-treechildren::-moz-tree-cell-text(unread) {
  color: var(--tabs-toolbar-color) !important;
  font-weight: bold !important;
}

#tt-treechildren::-moz-tree-row(pending-grayout),
#tt-treechildren::-moz-tree-cell-text(pending-grayout) {
  opacity: .5 !important;
}

#tt-treechildren::-moz-tree-row(selected, hover) {
  border: none !important;
  background-color: #39424D;
}

#tt-treechildren::-moz-tree-cell-text(hover) {
  text-decoration: none;
}

#tt-treechildren::-moz-tree-row(quickSearch) {
  border: none !important;
  border-right: 3px solid #D7F1FF !important;
}


.tt-new-tab-button {
  padding: 5px !important;
}
