css:
@-moz-document domain("facebook.com") {
html:before {
	content: "Don't waste your time!";
    color: #414141;
    opacity: 0;
    font-size: 4rem;
    top: 36vh;
    text-align: center;
    position: fixed;
    display: block;
    left: 0;
    right: 0;
    animation: blicking 1s infinite;
    animation-delay: 2s;
    z-index: 10000;
    text-shadow: 0px 0px 200px rgba(255, 255, 255, .75);
}

@keyframes blicking {
    0% { opacity: 0; }
    30% {opacity: 1; }
    60% {opacity: 1; }
    100% { opacity: 0; }
}
}
