css:
@-moz-document domain('wikipedia.org') {


/* DARKER LINKS THROUGHOUT FOR LESS DISTRACTION */

#bodyContent a:not(.new):visited {
    color: navy !important;
}
#bodyContent a:not(.new) {
    color: navy !important;
}
a:visited {
    color: navy !important;
}


/* HIDE FOOTNOTE/ REFERENCE LINKS */

sup.reference a {
    Display: none;
}
    
}
