css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
.post-activity-popover {
    width: 450px;
}

.post-activity-popover .popover_inner, .post-activity {
    width: 100% !important;
}

.post-activity-rollups .rollup-note-list {
    display: none;
}

.post-activity-rollups .rollup-notes-summary {
    text-align: center !important;
    cursor: pointer;
}

.post-activity .post-activity-note-content {
    width: 90%;
}

.post-activity .note-content-inner {
    width: 100%;
    max-width: 100% !important;
    max-height: 512px !important;
}

.post-activity .note-added-text {
    max-width: 100% !important;
}
}
