css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("stackoverflow.com"), domain("stackexchange.com"), domain("superuser.com") {
/* Have the question title width equal to the content with */
div#question-header {
    width: 728px;
}


/* Hide the sidebar completely to avoid distraction */
div#sidebar {
    display: none;
}


/* Hide the left sidebar completely to avoid distraction */
div#left-sidebar {
    display: none;
}


/* Make the used page width more like the content width 
  *  The original values are commented out
  */
html {
    /*min-width: 1060px;*/
    min-width: 728px;
}
body {
    /*min-width: 1075px;*/
    min-width: 743px;
}

#header {
    /*width: 1060px;*/
    width: 728px;
}
#content {
    /*width: 1060px;*/
    width: 728px;
}


/* This is the main content area for the questions (list, Q&A) */
#mainbar,
.mainbar {
    /*width: 728px;*/
    width: 728px;
}
.footerwrap {
    /*width: 1060px;*/
    width: 728px;
}

#footer #footer-sites {
    /*width: 1060px;*/
    width: 728px;
}

#system-message {
    /*width: 1060px;*/
    width: 728px;
}


/* cards are originally too wide to fit the smaller area */
.doctag-card {
    /*width: 250px;*/
    width: 167px;
}
}

@-moz-document domain("stackoverflow.com") {
  /* Make the stackoverflow logo size & position appropriate for the narrower space available 
  *  This works on stackoverflow.com
  */
  #hlogo a {
    background-size: 180px auto;
    top: 12px;
  }
}
