css:
@-moz-document domain("prntscr.com"), domain("prnt.sc") {

body, html {
    background: #111;
}
.m-pagetype_image .footer, .image-info-item, .image-info, .social, .extra, .uv-icon.uv-bottom-right, .footer {
    display: none;
}
.image__pic {
    background: #333;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    border-radius: 0;
}
.header {
    background: #111;
    top: -30px;
    transition: 0.7s ease;
    -webkit-transition: 0.7s ease;
    -moz-transition: 0.7s ease;
}
.header:hover {
    top: 0;
    background: #1b1b1b;
}
.button-blue .button__wrap, .button_blue .button__wrap, .button_blue_download .button__wrap, .button_blue_mac .button__wrap, .button_blue_win .button__wrap {
    background: #222;
}
.uploader__title, .uploader__progressbar_title, .uploader__success_title {
    color: #fff;
    color: rgb(255, 255, 255);
    text-shadow: none;
}
.twitter-header__title {
    font-weight: normal;
    color: #fff;
    opacity: 1;
    text-shadow: 0 1px 0 #000;
}
.twitter-header__title_small {
    color: #fff;
    opacity: 1;
    text-shadow: 0 1px 0 #000;
}
.loaded-info__since {
    opacity: 1;
    color: #fff;
}
.twitter-item__text {
    color: #fff;
}
.loaded-info__numbs {
    color: #fff;
    text-shadow: none;
}
.loaded-info__main {
    opacity: 1;
    font-weight: normal;
    color: #fff;
}
.twitter__more {
    display: block;
    background: rgba(0,0,0,0.2);
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    border-radius: 0;
    opacity: 1;
    color: #fff;
    text-shadow: 0 1px 0 #000;
}
.uploader__browse_button {
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    border-radius: 0;
    background: #222;
    color: #fff;
    font-weight: normal;
    text-shadow: none;
    box-shadow: 0 -2px 0 #000 inset;
    transition: 0.3s ease;
    -webkit-transition: 0.3s ease;
    -moz-transition: 0.3s ease;
}
.uploader__browse_button:hover {
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    background: #2b2b2b;
}
}
