css:
@-moz-document domain(trello.com) {
	body > div.window-overlay > div > div > div > div.window-sidebar > div:nth-child(4) > p > a {
    border: #000 solid 1px;
    background-color: white;
    color: black;
    padding: 20px;
    font-size: 15pt;
    box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 0.75);
    text-align: center;
    text-decoration: initial;
	}
	body > div.window-overlay > div > div > div > div.window-sidebar > div.window-module.other-actions.u-clearfix > div > a.button-link.js-archive-card {
    background-color: red;
    border-color: red;
	}
}
