css:
@-moz-document url-prefix("https://www.tumblr.com/customize") {
* {
    text-shadow: none !important;
    color: #4c4c4c !important;
}

.view_container, .secondary_bar, .title_bar {
    background-color: #8fafce !important;
}

.fieldset {
    background-color: rgba(223,243,254, .5);
}

.ace-tumblr .ace_marker-layer .ace_selection {
    background-color: #869ad5 !important;
}


.ace-tumblr .ace_active-line { 
    background-color: rgba(253, 248, 255, .1);
}

.button.blue {
    background-color: #dff3fe !important;
    border-color: #dff3fe !important;
}

.button.green {
    background-color: #ccefd1 !important;
    border-color: #ccefd1 !important;
}

.button.dark_gray {
    background-color: #c2b8d0 !important;
    border-color: #c2b8d0 !important;
}

.ace-tumblr .ace_string, .icon_arrow_thin_left:before {
    color: #ddd2e5 !important;
}

.ace-tumblr .ace_variable, .ace-tumblr .ace_support {
    color: #686778 !important;
}

.ace_keyword {
    color: #ccefd1 !important;
}

.ace_css_keyword, .ace-tumblr .ace_css_constant, .ace_css_variable {
    color: #c2b8d0 !important;
}

.ace-tumblr .ace_constant.ace_numeric, .ace_numeric, .ace-tumblr .ace_constant.ace_boolean {
    color: #dff3fe !important;
}

.ace-tumblr .ace_attribute-equals {
    color: #f2f2f2 !important;
}

.ace-tumblr .ace_storage.ace_type {
    color: #e5e5e5 !important;
}

.ace_scrollbar-inner::-webkit-scrollbar {
    overflow-x: hidden;
}

.ace_scrollbar-inner::-webkit-scrollbar-thumb {
    overflow-x: hidden;
}

.scrollbar_pill {
    background-color: rgba(221,210,229, .7) !important;
}

::-webkit-scrollbar {
    width: 9px;
    background: rgba(253, 248, 255, .4) !important;
}

::-webkit-scrollbar-thumb {
    height: 50px;
    background: rgba(253, 248, 255, .7) !important;
    -webkit-border-radius: 1ex;
}
}
