css:
@-moz-document url("https://www.tumblr.com/dashboard") {
body.af2k16, body.af2k16 .l-container.l-container--two-column-dashboard .left_column, body.af2k16 .l-container.l-container--two-column-dashboard .right_column, body.af2k16 .l-container.l-container--two-column-dashboard .l-content, body.af2k16 .post_avatar, body.af2k16 .post_avatar .post_avatar_link, body.af2k16 .l-header-container--refresh{
    background-color: #36465d !important;
}

.compose-button {    
    fill: #529ecc !important;
}

body.af2k16 .compose-button:before{
    display:none !important;
}

body.af2k16 .right_column:after{
    display:none !important;
}

.identity {
    background: #36465d !important;
}

body.af2k16 .decision-marquee .breaking-banner{
    display:none;
}

body.af2k16 .decision-marquee .marquee-headline {
    display:none !important;
}

body.af2k16 .decision-marquee .marquee-headline-content{
    display:none !important;
}

body.af2k16 .decision-marquee .ticker-title{
    display:none !important;
}

body.af2k16 .decision-marquee .marquee-item{
    display:none !important;
}

body.af2k16 .decision-marquee .ticker{
    display:none !important;
}

body.af2k16 .decision-marquee .marquee-gradient{
    display:none !important;
}

.post.new_post {
    background-color:#fff !important;
}

body.af2k16 .new_post_label_text{
    color:#696969 !important;
}
}
