css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.reddit.com/r/Undertale"),
               url-prefix("https://www.reddit.com/r/undertale"),
               url-prefix("http://www.reddit.com/r/Undertale"),
               url-prefix("http://www.reddit.com/r/undertale")
{
.midcol.unvoted, .midcol.likes, .midcol.dislikes {
    width: 18px;
}

.arrow {
    width: 18px;
    height: 17px;
}
.arrow.up {
    background-position: -1px 0;
    background-image: url(http://i.imgur.com/5P1VLJz.png);
    background-size: 80px;
}
.arrow.upmod {
    background-position: -21px 0;
    background-image: url(http://i.imgur.com/5P1VLJz.png);
    background-size: 80px;
}
.arrow.down {
    background-position: -41px 0;
    background-image: url(http://i.imgur.com/5P1VLJz.png);
    background-size: 80px;
}
.arrow.downmod {
    background-position: -61px 0;
    background-image: url(http://i.imgur.com/5P1VLJz.png);
    background-size: 80px;
}
}
