css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("new.vk.com")
{

  .im_grid > img  {display: none!important;}  /* скрываем фоты */
  
  .nim-dialog--photo {
    background-image: url("http://vampireos.ru/uploads/avatars/spycats.svg"); /* ставим нашего спайкотэ в списке слева */
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    border-radius: 10px;
    height: 40px;
    width: 40px;
    position: relative;
    top:3px;
  }
  
  .nim-dialog--name-w > span {
    color: transparent!important;  /* делаем текст ИМЁН слева прозрачным */
    text-shadow: 0 0 7px #a8bbcc!important;  /* размываем текст */
  }
  
  .nim-dialog--preview._dialog_body {
    color: transparent!important;  /* делаем текст СООБЩЕНИЯ слева прозрачным */
    text-shadow: 0 0 7px #a8bbcc!important;  /* размываем текст */
  }
  
  .im-mess-stack--photo { 
    background-image: url("http://vampireos.ru/uploads/avatars/spycats.svg"); /* ставим нашего спайкотэ в сообщениях */
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    border-radius: 10px;
    height: 40px;
    width: 40px;
    position: relative;
  }
  
  .im-mess-stack .im-mess-stack--content .im-mess-stack--pname a {
    color: transparent!important;
    text-shadow: 0 0 7px #a8bbcc!important;  /* размываем текст имени справа */
  }
  
  .im-page--peer._im_page_peer_name { 
    color: transparent!important;
    text-shadow: 0 0 7px #a8bbcc!important;  /* размываем текст имени собеседника вверху */
  } 
  
  .top_profile_name {
    color: transparent!important;
    text-shadow: 0 0 7px #a8bbcc!important;  /* размываем текст своего имени вверху */
  }
  
}

/* https://vk.com/vampireos ^ ^ */
