/* SCROLL BAR BY PAWNERZ */
/*     vk.com/pawnerz    */
::-webkit-scrollbar{height:6px;}
::-webkit-scrollbar{width:6px;}
::-webkit-scrollbar-track-piece:vertical{background-color: rgba(0, 0, 0, 0.25);}
::-webkit-scrollbar-track-piece:horizontal{background-color: rgba(0, 0, 0, 0.25);}
::-webkit-scrollbar-thumb:vertical{background-color:rgba(0, 0, 0, 0.25);border-radius:2px;-webkit-border-radius:2px;}
::-webkit-scrollbar-thumb:horizontal{background-color:rgba(0, 0, 0, 0.25);border-radius:2px;-webkit-border-radius:2px;}
