css:
@-moz-document domain("www.ceneo.pl") {
body {
    background-color: black !important;
    color: white;
}
.page-body {
    background-color: inherit !important;
}
.header-container {
    background-color: #26559C;
}
.services-links a {
    color: white;
}
.header-basket .shopping-cart--icon {
    color: white;
}
#body {
    background-color: #4E2C09;
}
h1,
h2,
h3,
h4,
h5,
h6,
a {
    color: white;
}
.main-menu .cat-menu {
    background-color: #5A1900;
}
input,
textarea,
.textarea,
select {
    color: #777;
    background-color: #000;
}
footer {
    background-color: #666;
}
.footer {
    background-color: rgba(0, 0, 0, 0.33);
}
.market-link svg {
    fill: #B75500;
}
.carousel-menu li.active {
    color: #CDDBE8;
    border-color: #CDDBE8;
}
.active-filters {
    background-color: #4B88B1;
    border: #4B88B1;
}
.sub-header {
    color: #000;
}
.cat-prod-row {
    background-color: black;
}
.breadcrumbs .breadcrumb {
    background-color: inherit;
}
.breadcrumbs dd a {
    color: #73BCF3;
}
.filter-bar {
    background-color: #3394D4;
}
.dropdown-wrapper .dropdown-toggle {
    background-color: #000;
}
.page-tabs {
    background-color: #26559C;
}
.full-specs-footer {
    background-color: rgba(0, 0, 0, 0.33);
}
.section-header h2,
.section-header h3,
.section-header h4 {
    color: #BBBBBB;
}
}
