css:
@-moz-document domain("wired.com"), domain("doubleclick.net") {
.rad.brad-top,
.rad.box.sidebar-rad,
.advertisement,
.rad.mid-banner-wrap,
#around-the-web,
#we-recommend,
#adunit,
#ads {
    display: none !important;
}
.ui-social-wrapper.gallery {
    display: none;
}
.ui-social-wrapper.image {
    display: none;
}
#sidebar {
    display: none;
}
#footer-social {
    display: none;
}
#cm-footer {
    display: none;
}
#share-card {
    display: none;
}
#sharing {
    display: none;
}
#latest-news {
    display: none;
}
#prefooter {
    display: none;
}
.big-col-7 {
    display: none;
}
body.short-post .post {
    left: 0px;
}
#post-header.standard:not(.short-post-header) {
    padding-right: 0;
}
.wide .post-container,
.fullbleed .post-container,
body:not(.wide-post):not(.short-post) .post {
    left: 0px;
}
ody:not(.legacy-content):not(.short-post):not(.listicle) .content {
    left: 0px;
    margin-right: 0px;
}
.site-container {
    max-width: 96%;
}
.paddle .paginate-box.left {
    width: 45px;
}
.paddle .paginate-box.right {
    width: 45px;
}
}
