css:
@-moz-document domain("cultofandroid.com") {
.share {
    display: none;
}
#sidebar {
    display: none;
}
#content-wrapper {
    margin-left: 0px;
    margin-right: 0px;
}
#sidebar-content {
    display: none;
}
#masthead {
    margin-left: 0px;
    margin-bottom: 0px;
    padding: 0 0px 0px;
    background-color: black;
}
#masthead nav ul {
    background: #499632;
}
.post {
    border-top-color: #499632;
}
#article-wrapper {
	display: none;
}
#content-wrapper .author h3 {
    color: #980D0D;
}
.post .author h3 a {
    color: #980D0D;
}
body {
    background-color: black;
}
p {
    color: #777;
}
a {

    color: #6877EC;
    border-bottom: none;
}
.wp-caption {
    background: black;
}
}
