css:
@-moz-document domain("twitchalerts.com") {
.docklet 
{
    color:white;
}
element.style
{
    color: white;
}

.docklet strong
{
    color: white;
}
.docklet .heading
{
    background: black;
    color: white;
}

.checkbox .checkbox-inner+span
{
    color: white;
}
.recent-events-list > div
{
    background: none repeat scroll 0 0 #191919;
}


.recent-events-list > div.even
{
    background: none repeat scroll 0 0 #242424;
}
}
