css:
@-moz-document domain(apidock.com) {
  body, #content, div#content_wrapper {
    background-color: black !important;
    color: gray !important;
  }
  
  pre, #flowdock-tab, #navigation li, #nav_search_field, #search_results, #source_link {
    -webkit-filter: invert(100%) hue-rotate(180deg) !important;
  }
  
  .note {
    border-color: #262626 !important;
  }
}
