css:
@-moz-document domain("snapster.io") { 
.button.button_fill {
    color: transparent;
    background: #00aee2;
    position: fixed;
    width: 65px;
    height: 65px;
    padding: 16px 24px;
    bottom: 30px;
    right: 30px;
    border-radius: 100%;
    transition: 0.2s ease;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.26);
}
.button.button_fill:active {
    box-shadow: 0 8px 17px 0 rgba(0, 0, 0, 0.2);
}
.room_panel_upload_button span {
    color: white;
}

.page_wrap_message, .room_view_not_photos {
    background: url(http://vk.com/images/no_posts.png) no-repeat 50% 90px !important;
}
}
