css:
@-moz-document domain("wccftech.com") {
body {
    background-color: black;
    color: #b34700;
}
.sticky a {
    color: #b34700;
}
.post {
    border-bottom: 1px solid #b34700;
}
h1 a, h2 a, h3 a {
    color: #b34700;
}
.above-the-fold {
    background: black;
    padding: 20px 0 40px 0;
    margin-bottom: 38px;
}
main {
    position: relative;
    padding: 50px;
    background-color: black;
    margin-bottom: 50px;
}
.sticky h2 span {
    font-weight: 500;
    background: black;
    padding: 0 10px 0 0;
}
.sticky h2 span {
    color: orange;
}
.featured a {
    color: #b34700;
}
#footer1_wrap {
    background: #b34700;
}
.featured {
    color: orange;
}
.sticky li.first a {
    color: orange;
}
.sticky h2 span {
    color: black;
}
.content {
    background: black;
}
.highlights {
    background: black;
}
.highlights h2 span {
    background: black;
}
footer h3 {
    color: orange;
}
header {
    border-bottom: 1px solid gray;
    background: white;
    background: black;
}
#content blockquote {
    background: #262626;
}
body.dark {
    color: #b34700;
}
.body p, .body ul, .body ol, .body table {
    margin-bottom: 15px;
    line-height: 1.7;
    color: orange;
}
.topic.hardware {
    background: #262626;
}
.topic.games {
    background: #262626;
}
.topic.gadgets {
    background: #262626;
}
}
