css:
/* i really want this to be global */

.jipt-highlight .crowdin_jipt_untranslated {
    outline: 3px #FF3D3D solid !important;
}
.jipt-highlight .crowdin_jipt_translated {
    outline: 3px #3D87FF solid !important;
}
.jipt-highlight .crowdin_jipt_approved {
    outline: 3px #6AC953 solid !important;
}
.jipt-highlight .crowdin_jipt_hidden,
.jipt-highlight .crowdin_jipt_not_found {
    outline: 3px #999 solid !important;
}
